import { useState } from "react";
import {
  GoogleMap,
  LoadScript,
  Marker,
  InfoWindow,
} from "@react-google-maps/api";

const mapContainerStyle = {
  width: "100%",
  height: "400px",
};

const center = {
  lat: 53.54,
  lng: 10,
};

export default function Intro() {
  const [open, setOpen] = useState(false);

  const onMarkerClick = () => {
    setOpen(true);
  };

  const onCloseClick = () => {
    setOpen(false);
  };

  return (
    <LoadScript googleMapsApiKey={AIzaSyDEzFZ7S5HahRWdo8LcBugTHxbyGdWUmFU}>
      <GoogleMap mapContainerStyle={mapContainerStyle} zoom={9} center={center}>
        <Marker position={center} onClick={onMarkerClick} />

        {open && (
          <InfoWindow position={center} onCloseClick={onCloseClick}>
            <p>I'm in Hamburg</p>
          </InfoWindow>
        )}
      </GoogleMap>
    </LoadScript>
  );
}

